import React, { useState } from "react";
import * as Yup from "yup";

import { Formik, Form, Field, ErrorMessage } from "formik";

import { FieldDiv } from "./styles";
import { FormLabel, FormField } from "../../../shared/styles/styles";

import Summary from "./tokensummary/summary";

import { CommonHeading } from "../../../shared/styles/styles";
import { Container } from "./styles";

import { Row, Col } from "antd";

const SignupSchema = Yup.object().shape({
  accountName: Yup.string()
  .length(12,"jj")
    .required("Required"),

  // tokenSYMBOL: Yup.string()
  // .test('len', 'Must be More Than 3 characters', val => val.length >= 3)
  //   .required("Required"),
  // imaxSupply: Yup.string()
  // // .test('len', 'Must be exactly 5 characters', val => val.length === 5)
  //   .required("Required"),
  // issueTokens: Yup.string()
  // // .test('len', 'Must be exactly 5 characters', val => val.length === 5)
  //   .required("Required"),
  // issueAccount: Yup.string()
  // .test('len', 'Must be  12 characters', val => val.length === 12)
  //   .required("Required")
});

export default function FreeToken() {
  const [data, setData] = useState({});

  // function onSubmit(values: any) {
  //   console.log("submitted and it is working fine--->");
  //   console.log("values-->", values);
  //   setData(values);
  // }

  return (
    <Container>
      <CommonHeading>FREE TOKEN FEATURES</CommonHeading>
      <Row>
        <Col xs={{ span: 24, offset: 0 }} lg={{ span: 8, offset: 8 }}>
          <Formik
            initialValues={{
              accountName: "",
              tokenSYMBOL: "",
              maxSupply: "",
              issueTokens: "",
              issueAccount: ""
            }}

            validationSchema={SignupSchema}
            onSubmit={values => {
              // same shape as initial values
              console.log("values--->",values);

            }}
          >
            {({ errors, touched }) => (
              <Form>
                <FieldDiv>
                  <FormLabel>Token Account Name</FormLabel>
                  <FormField
                    name="accountName"
                    type="text"
                    placeholder="Must Be 12 Characters"
                  />
                  {errors.accountName && touched.accountName ? (
                    <div>{errors.accountName}</div>
                  ) : null}
                </FieldDiv>
                <FieldDiv>
                  <FormLabel>Token Symbol</FormLabel>
                  <FormField
                    name="tokenSYMBOL"
                    type="text"
                    placeholder="E.G RAPID"
                  />
                  {errors.tokenSYMBOL && touched.tokenSYMBOL ? (
                    <div>{errors.tokenSYMBOL}</div>
                  ) : null}
                </FieldDiv>
                <FieldDiv>
                  <FormLabel>Max Supply</FormLabel>
                  <FormField
                    name="maxSupply"
                    type="text"
                    placeholder="Must Be 12 Characters"
                  />
                  {errors.maxSupply && touched.maxSupply ? (
                    <div>{errors.maxSupply}</div>
                  ) : null}
                </FieldDiv>
                <FieldDiv>
                  <FormLabel>Issue Tokens</FormLabel>
                  <FormField
                    name="issueTokens"
                    type="text"
                    placeholder="How Many Token Do You Want To Issue?"
                  />
                  {errors.issueTokens && touched.issueTokens ? (
                    <div>{errors.issueTokens}</div>
                  ) : null}
                </FieldDiv>
                <FieldDiv>
                  <FormLabel>Issue Account</FormLabel>
                  <FormField
                    name="issueAccount"
                    type="text"
                    placeholder="Default Will Issue To (TokenAccountName)"
                  />
                  {errors.issueAccount && touched.issueAccount ? (
                    <div>{errors.issueAccount}</div>
                  ) : null}
                </FieldDiv>

                <button type="submit">Submit</button>
              </Form>
            )}
          </Formik>
        </Col>

        <Col xs={{ span: 24 }} lg={{ span: 8 }}>
          <Summary data={data} />
        </Col>
      </Row>
    </Container>
  );
}
